      value = [[dic objectForKey:key] intValue];    }    return value;}//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////-(float)setKeyFloat:(id)key Dic:(NSDictionary *)dic{    float value=0;    if([dic objectForKey:key] && [dic objectForKey:key] != [NSNull null])    {        value = [[dic objectForKey:key] floatValue];    }    return value;}//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////-(BOOL)setKeyBool:(id)key Dic:(NSDictionary *)dic{    BOOL value=NO;    if([dic objectForKey:key] && [dic objectForKey:key] != [NSNull null])    {        value=[[dic objectForKey:key] boolValue];    }    return value;}@end